using System.Collections;using System.Collections.Generic;using UnityEngine;public class Girar : MonoBehaviour{    // Start is called before the first frame update    void Start()    {            }    public bool counterClockwise;    public byte modVel = 1;        // Update is called once per frame    void FixedUpdate()    {        float floatVelMod = (modVel / 10) * -1;        if (modVel == 0) goto noRotation;        if (counterClockwise) floatVelMod = floatVelMod * -1;        if (counterClockwise)        {            transform.Rotate(0, 0, 1 + floatVelMod);        }        else        {            transform.Rotate(0, 0, -1 + floatVelMod);        }    noRotation:;    }}